import React from "react";
import Sidebar from "react-sidebar";
import { Container, Row, Col } from 'reactstrap';
import { ResponsiveLine } from '@nivo/line'
import { ResponsiveBar } from '@nivo/bar'
import { ResponsivePieCanvas } from '@nivo/pie'
import {works, doesNotWork, line_data} from "./data";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebarOpen: true,
        selected: 0,
      currentTopic:"ACTF"
    };
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }

    changeTitle = (title) => {
        this.setState(
            { currentTopic: title,
                sidebarOpen: false }
            );
    }

    changeColor = (number) => {
        this.setState(
            { selected: number}
        );
    }

    myColor =(position) => {
        if (this.state.selected === position) {
            return "grey";
        }
        return "";
    }

  render() {
    return (
        <Sidebar
            sidebar={
              <div styles={{ height: '500px', overflowY: 'scroll', flexDirection: 'column' }} >
              <Row>
              <h2 style={{background: this.myColor(0)}} onClick={() => this.changeTitle("ACTF")} onMouseEnter={() => this.changeColor(0)}>
                ACTF
              </h2>
              </Row>
              <Row>
              <h2 style={{background: this.myColor(1)}} onClick={() => this.changeTitle("Coronavirus")} onMouseEnter={() => this.changeColor(1)}>
                Coronavirus
              </h2>
              </Row>
              <Row>
              <h2 style={{background: this.myColor(2)}} onClick={() => this.changeTitle("Housing")} onMouseEnter={() => this.changeColor(2)}>
                Housing
              </h2>
              </Row>
              <Row>
              <h2 style={{background: this.myColor(3)}} onClick={() => this.changeTitle("Recruiting")} onMouseEnter={() => this.changeColor(3)}>
                Recruiting
              </h2>
              </Row>
              </div>
            }
            open={this.state.sidebarOpen}
            onSetOpen={this.onSetSidebarOpen}
            styles={{ sidebar: { background: "white" } }}
        >
            <h1>
                {this.state.currentTopic}
            </h1>
            <button onClick={() => this.onSetSidebarOpen(true)}>
                Select topic
            </button>
            <React.Fragment>
                <h2>Activities</h2>
                {linechart(line_data)}
            </React.Fragment>

        </Sidebar>
    );
  }
}
function linechart(data) {
    return (
        <ResponsiveLine
            data={data}
            margin={{
                top: 0,
                right: 50,
                bottom: 50,
                left: 50
            }}
            yScale={{
                type: "linear",
                stacked: false,
                min: 0
            }}
            xScale={{
                type: "time",
                precision: "day",
                format: "native"
            }}
            axisBottom={{
                format: "%b %d"
            }}
        />
    );
}

function piechart(data) {
    return (
        <ResponsiveLine
            data={data}
            margin={{
                top: 0,
                right: 50,
                bottom: 50,
                left: 50
            }}
            yScale={{
                type: "linear",
                stacked: false
            }}
            xScale={{
                type: "time",
                precision: "day",
                format: "native"
            }}
            axisBottom={{
                format: "%b %d"
            }}
        />
    );
}
// const styles = StyleSheet.create({
//
// });

export default App;
