import React from "react";
import Sidebar from "react-sidebar";
import { Container, Row, Col } from 'reactstrap';
import { ResponsiveLine } from '@nivo/line'
import { ResponsiveBar } from '@nivo/bar'
import { ResponsivePieCanvas } from '@nivo/pie'
import {pie_data, line_data, line_pos_neg,activity_pie_data} from "./data";
import './App.css'
import GaugeChart from 'react-gauge-chart'
import Thermometer from 'react-thermometer-component'

var API = 'http://127.0.0.1:8000/polar_result/';
var date = 'Feb_01_2020';
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    	error: null,
    	isLoaded: false,
    	sidebarOpen: true,
        selected: 0,
    	currentTopic:"ACFT",
    	positive: 0,
    	negative: 0,
    	neutral: 0,
    	total: 0,
    	threshold: 100,
    };
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  componentDidMount() {
  	fetch(API + this.state.currentTopic + '/' + date)
  	.then(res => res.json())
  	.then(
  		(result) => {
  			this.setState({
  				isLoaded: true,
  				positive: result.positive,
  				negative: result.negative,
  				neutral: result.neutral,
  				total: result.positive + result.negative + result.neutral,
  			})
  			console.log(result);
  		}

  		);
  }
  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }

    changeTitle = (title) => {
        this.setState(
            { currentTopic: title,
                sidebarOpen: false }
            );
    }

    changeColor = (number) => {
        this.setState(
            { selected: number}
        );
    }

    myColor =(position) => {
        if (this.state.selected === position) {
            return "grey";
        }
        return "";
    }

  render() {
  	const { error, isLoaded} = this.state;
  	 if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
	    return (
	        <Sidebar
	            sidebar={
	              <div styles={{ height: '500px', width:'500px', overflowY: 'scroll', flexDirection: 'column' }} >
	              <Row>
	              <h2 style={{background: this.myColor(0)}} onClick={() => this.changeTitle("ACFT")} onMouseEnter={() => this.changeColor(0)}>
	                ACTF
	              </h2>
	              </Row>
	              <Row>
	              <h2 style={{background: this.myColor(1)}} onClick={() => this.changeTitle("Coronavirus")} onMouseEnter={() => this.changeColor(1)}>
	                Coronavirus
	              </h2>
	              </Row>
	              <Row>
	              <h2 style={{background: this.myColor(2)}} onClick={() => this.changeTitle("Housing")} onMouseEnter={() => this.changeColor(2)}>
	                Housing
	              </h2>
	              </Row>
	              <Row>
	              <h2 style={{background: this.myColor(3)}} onClick={() => this.changeTitle("Recruiting")} onMouseEnter={() => this.changeColor(3)}>
	                Recruiting
	              </h2>
	              </Row>
	              </div>
	            }
	            open={this.state.sidebarOpen}
	            onSetOpen={this.onSetSidebarOpen}
	            styles={{ sidebar: { background: "white" } }}
	        >
	            <h1>
	                {this.state.currentTopic}
	            </h1>
	            <button onClick={() => this.onSetSidebarOpen(true)}>
	                Select topic
	            </button>
	            <div className='charts'>
		            <GaugeChart id="gauge-chart1" 
		              nrOfLevels={2} 
		              animate={false}
		              percent={(2 * this.state.positive + 1 * this.state.neutral)/(2 * this.state.total)}
		            />
		            <GaugeChart id="gauge-chart2" 
		              nrOfLevels={1} 
		              animate={false}
		              percent={this.state.total/this.state.threshold}
		            />
		            <div className='temperature'>
			            <Thermometer
						  theme="light"
						  value="18"
						  max="100"
						  size="normal"
						  height="150"
						/>
					</div>
	            </div>
	        </Sidebar>
	    );
	  }
	}
}

